  import React from 'react';
  import { mdx } from '@mdx-js/react';

  export const frontMatter = {
	id: 'register-input',
	description: 'Normally when we want to make a form we should think about which approach we are going to use; controlled or uncontrolled or debounce input.',
	title: 'Register Input with useForm.'
};
export const metadata = {
  "unversionedId": "register-input",
  "id": "register-input",
  "isDocsHomePage": false,
  "title": "Register Input with useForm.",
  "description": "Normally when we want to make a form we should think about which approach we are going to use; controlled or uncontrolled or debounce input.",
  "source": "@site/docs/register-input.mdx",
  "slug": "/register-input",
  "permalink": "/docs/register-input",
  "editUrl": "https://github.com/Jucian0/useform/tree/docs/docs/register-input.mdx",
  "version": "current"
};
  /* @jsxRuntime classic */
/* @jsx mdx */

export const rightToc = [{
  value: 'Usage',
  id: 'usage',
  children: [{
    value: 'input function',
    id: 'input-function',
    children: []
  }, {
    value: 'custom function',
    id: 'custom-function',
    children: []
  }]
}];

const layoutProps = {
  rightToc
};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">

    <p>{`Normally when we want to make a form we should think about which approach we are going to use; controlled or uncontrolled or debounce input. When we use useForm we can make the three aproach the same way, it is possible because useform has a function to register inputs and that function can make all kinds of input.`}</p>
    <h2 {...{
      "id": "usage"
    }}>{`Usage`}</h2>
    <h3 {...{
      "id": "input-function"
    }}>{`input function`}</h3>
    <p>{`The input function registers an input HTML tag. You can pass input tag or input name and type properties respectively.`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-jsx"
      }}>{`   import {useForm} from 'useforms'

   const [{values, reset, onSubmit},{input}] = useForm({onChange:true, initialValues})

   <input {...input({name:"input-name", type:"text", required:true ....})}>
                     /*or*/
   <input {...input("input-name", "text")}>
`}</code></pre>
    <h3 {...{
      "id": "custom-function"
    }}>{`custom function`}</h3>
    <p>{`The `}<inlineCode parentName="p">{`custom`}</inlineCode>{` function registers a custom component like Custom Selects, Custom DatePiker. Custom inputs change your internal state every time the onChange event is dispatched, this means the `}<inlineCode parentName="p">{`custom`}</inlineCode>{` function not support uncontrolled inputs.`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-jsx"
      }}>{`   import {useForm} from 'useforms'

   const [{values, reset, onSubmit},{custom}] = useForm({onChange:true, initialValues})

   <CustomINput {...custom("custom-select")}>

`}</code></pre>
    </MDXLayout>;
}

;
MDXContent.isMDXComponent = true;
