  import React from 'react';
  import { mdx } from '@mdx-js/react';

  export const frontMatter = {
	id: 'quick-start',
	title: 'Quick Start',
	description: 'useForm provides the better way to create advanceds forms easily.also provides a simple way to validate form inputs. Try this!!!',
	sidebar_label: 'Quick Start'
};
export const metadata = {
  "unversionedId": "quick-start",
  "id": "quick-start",
  "isDocsHomePage": false,
  "title": "Quick Start",
  "description": "useForm provides the better way to create advanceds forms easily.also provides a simple way to validate form inputs. Try this!!!",
  "source": "@site/docs/quick-start.mdx",
  "slug": "/quick-start",
  "permalink": "/docs/quick-start",
  "editUrl": "https://github.com/Jucian0/useform/tree/docs/docs/quick-start.mdx",
