export const rightToc = [];

const layoutProps = {
  rightToc
};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">

    <p><inlineCode parentName="p">{`useValidations`}</inlineCode>{` is a custom hook that provides a yup validation. This hook provides an object with two properties: errors and isValid.
`}<inlineCode parentName="p">{`useValidation`}</inlineCode>{` will be called every time when values are changed.`}</p>
    <table>
      <thead parentName="table">
        <tr parentName="thead">
          <th parentName="tr" {...{
            "align": null
          }}>{`Property`}</th>
          <th parentName="tr" {...{
            "align": "center"
          }}>{`Description`}</th>
        </tr>
      </thead>
      <tbody parentName="table">
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}><inlineCode parentName="td">{`errors`}</inlineCode></td>
          <td parentName="tr" {...{
            "align": "center"
          }}>{`Object with all errors, this object is the same shape of object values.`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}><inlineCode parentName="td">{`isValid`}</inlineCode></td>
          <td parentName="tr" {...{
            "align": "center"
          }}>{`Boolean property`}</td>
        </tr>
      </tbody>
    </table>
    <pre><code parentName="pre" {...{
        "className": "language-jsx"
      }}>{`const validation = yup.object().shape({
  name: yup.string().required('This field is required'),
  email: yup
    .string()
    .email('This field must be a valid e-mail')
    .required('This field is required'),
  pets: yup.array(yup.string().required('This field is required')),
  accept: yup.bool().oneOf([true], 'Field must be checked')
})

const { errors, isValid } = useValidation(values, validation)
`}</code></pre>
    </MDXLayout>;
}

;
MDXContent.isMDXComponent = true;
