  import React from 'react';
  import { mdx } from '@mdx-js/react';

  export const frontMatter = {
	id: 'custom-input',
	description: 'useForm provides a way to use custom inputs like react-date-picker or react-select. The custom inputs receive generics properties, onChange, value, and property to simulate onBlur event. Use React Select, React Datepicker.',
	title: 'React form with Custom Input'
};
export const metadata = {
  "unversionedId": "custom-input",
  "id": "custom-input",
  "isDocsHomePage": false,
  "title": "React form with Custom Input",
  "description": "useForm provides a way to use custom inputs like react-date-picker or react-select. The custom inputs receive generics properties, onChange, value, and property to simulate onBlur event. Use React Select, React Datepicker.",
  "source": "@site/docs/custom-input.mdx",
  "slug": "/custom-input",
  "permalink": "/docs/custom-input",
  "editUrl": "https://github.com/Jucian0/useform/tree/docs/docs/custom-input.mdx",
  "version": "current"
};
  /* @jsxRuntime classic */
/* @jsx mdx */
import Custom from './../src/components/custom'
export const rightToc = [{
  value: 'Description',
  id: 'description',
  children: []
}, {
  value: 'Instalation',
  id: 'instalation',
  children: []
}, {
  value: 'Usage',
  id: 'usage',
  children: []
}, {
  value: 'Demo',
  id: 'demo',
  children: []
}];

const layoutProps = {
  rightToc
};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">


    <h2 {...{
      "id": "description"
    }}>{`Description`}</h2>
    <p>{`When we work with react, sometimes we need a custom element to provide advanced interaction with users,
like a custom date input, custom select or a tag creator,
for all these cases we can use many options of components provided from the community.
But these custom elements cannot simulate de same events like a native element,
for this reason, useForm cannot provide same experience what useForm provide with a native element like `}<inlineCode parentName="p">{`<input>`}</inlineCode>{`,
but we can use useForm to manage this kind of component like a controlled element.`}</p>
    <p>{`Custom inputs receive generics properties, onChange, value, and property to simulate onBlur event.
When you use a custom input every change that happened in the input state is replied in state of form component because this we cannot use fell the uncontrolled inputs in custom elements.`}</p>
    <h2 {...{
      "id": "instalation"
    }}>{`Instalation`}</h2>
    <pre><code parentName="pre" {...{}}>{`  npm install useforms
`}</code></pre>
    <pre><code parentName="pre" {...{}}>{`  yarn add useforms
`}</code></pre>
    <h2 {...{
      "id": "usage"
    }}>{`Usage`}</h2>
    <p><inlineCode parentName="p">{`useForm`}</inlineCode>{` provides a `}<inlineCode parentName="p">{`custom`}</inlineCode>{` function, this function as a link with input and a object property of form state.`}</p>
    <p>{`Use dot notation to create advanced objects or to map object values. Type an entry name and type or an entry property object.`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-jsx"
      }}>{`   import {useForm} from 'useforms'
   import Select from 'react-select'
   import DatePicker from "react-datepicker";

   const initialValues = {
      candies:{
         iceCream:[
            { value: 'chocolate', label: 'Chocolate' }
         ]
      },
      date: new Date("05/26/2020")
   }

   const options = [
      { value: 'chocolate', label: 'Chocolate' },
      { value: 'strawberry', label: 'Strawberry' },
      { value: 'vanilla', label: 'Vanilla' }
      ]      

   function ControlledForm(){

      const [{values, reset},{input}] = useForm({onChange:true, initialValues})
      return (
         <>
            <Select options={options} {...custom("candies.iceCream.0")} />
            <DatePicker
               className="form-control mw-100"
               showPopperArrow={false}
               selected={values.date}
               {...custom("date")}
            /> 
         </>   
      )
   }
`}</code></pre>
    <h2 {...{
      "id": "demo"
    }}>{`Demo`}</h2>
    <Custom mdxType="Custom" />
    </MDXLayout>;
}

;
MDXContent.isMDXComponent = true;
