  import React from 'react';
  import { mdx } from '@mdx-js/react';

  export const frontMatter = {
	id: 'get-starter',
	title: 'Get Starter',
	description: 'useForm provides the better way to create advanceds forms easily.also provides a simple way to validate form inputs. Try this!!!',
	sidebar_label: 'Get Starter with useForm'
};
