/* @jsx mdx */
import Controlled from './../src/components/controlled'
export const rightToc = [{
  value: 'Description',
  id: 'description',
  children: []
}, {
  value: 'Instalation',
  id: 'instalation',
  children: []
}, {
  value: 'Usage',
